import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import './Nav.scss';
import {NavItem} from './Nav';

function Nav() {
  const [ScrollY, setScrollY] = useState(0); 
  const [ScrollActive, setScrollActive] = useState(false);
  function  handleScroll() {
    if(ScrollY > 120) {
      setScrollY(window.pageYOffset);
      setScrollActive(true);
    } else {
      setScrollY(window.pageYOffset);
      setScrollActive(false);
    }    
  }
  // 스크롤시 메뉴 고정
  useEffect(() => {
    function scrollListener(){
      window.addEventListener('scroll',handleScroll);
    }
    scrollListener();
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  });

  // const [onClick, setNav] = useState(0); 
  // const [NavActive, setNavActive] = useState(false);

    return (
      <div className={ScrollActive ? "gnb__section fixed" : "gnb__section"}>      
        <h1>
          <Link to="/">
            봄이 오나         
          </Link>
        </h1>
        <ul className="gnb__list">
            <li><a href="#none">퍼블리싱</a>
                <ul>
                    <li><a href="#none">HTML</a></li>
                    <li><a href="#none">SCSS & CSS</a></li>
                </ul>
            </li>
            <li><a href="#none">스크립트</a>
                 <ul>
                    <li><a href="#none">자바스크립트</a></li>
                    <li><a href="#none">소스모음</a></li>
                </ul>            
            </li>
            <li><a href="#none">프론트엔드</a>
                <ul>
                    <li><a href="#none">React</a></li>
                    <li><a href="#none">Vue</a></li>
                </ul>               
            </li>
            <li><a href="#none">웹접근성</a>
                 <ul>
                    <li><a href="#none">주의사항</a></li>
                    <li><a href="#none">PC심사기준</a></li>
                    <li><a href="#none">모바일심사기준</a></li>
                </ul>               
            </li>
            <li><a href="#none">블로그</a></li>
        </ul>
      </div>

  );
}
export default Nav;
